(20065, 'wpseo_taxonomy_meta', 'a:1:{s:8:\"category\";a:11:{i:873;a:5:{s:11:\"wpseo_title\";s:14:\"%%term_title%%\";s:10:\"wpseo_desc\";s:247:\"Tìm hiểu các quy định pháp luật Việt Nam về xuất nhập khẩu: thủ tục hải quan, chính sách thuế, hàng hóa cấm và hạn chế xuất nhập khẩu theo Luật Thương mại và các văn bản pháp lý liên quan.\";s:13:\"wpseo_focuskw\";s:44:\"pháp luật xuất nhập khẩu Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"72\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:46;a:4:{s:10:\"wpseo_desc\";s:233:\"Tìm hiểu khái niệm, nguyên tắc và các quy định pháp luật dân sự Việt Nam. Cập nhật thông tin mới nhất về quyền và nghĩa vụ dân sự của cá nhân, tổ chức theo Bộ luật Dân sự 2015.\";s:13:\"wpseo_focuskw\";s:33:\"pháp luật dân sự Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:43;a:4:{s:10:\"wpseo_desc\";s:226:\"Khám phá các quy định pháp luật về đất đai tại Việt Nam: quyền sử dụng đất, chuyển nhượng, thừa kế, và tranh chấp đất đai theo Luật Đất đai 2013 và các văn bản liên quan.\";s:13:\"wpseo_focuskw\";s:35:\"pháp luật đất đai Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:42;a:4:{s:10:\"wpseo_desc\";s:202:\"Tìm hiểu quy định pháp luật về doanh nghiệp tại Việt Nam: thành lập, tổ chức quản lý, chuyển nhượng vốn và giải thể doanh nghiệp theo Luật Doanh nghiệp 2020.\";s:13:\"wpseo_focuskw\";s:38:\"pháp luật doanh nghiệp Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:874;a:4:{s:10:\"wpseo_desc\";s:258:\"Tìm hiểu các quy định pháp luật Việt Nam về giao thông vận tải: an toàn giao thông, xử phạt vi phạm, điều kiện kinh doanh vận tải và quy định mới theo Luật Giao thông đường bộ và các văn bản liên quan.\";s:13:\"wpseo_focuskw\";s:47:\"pháp luật giao thông vận tải Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"72\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:41;a:4:{s:10:\"wpseo_desc\";s:256:\"Khám phá pháp luật hành chính Việt Nam: cơ cấu tổ chức hành chính nhà nước, thủ tục hành chính, xử phạt vi phạm hành chính và quy định mới theo Luật Xử lý vi phạm hành chính và các văn bản liên quan.\";s:13:\"wpseo_focuskw\";s:36:\"pháp luật hành chính Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:45;a:4:{s:10:\"wpseo_desc\";s:210:\"Tìm hiểu pháp luật hình sự Việt Nam: các tội phạm, hình phạt, nguyên tắc truy cứu trách nhiệm hình sự theo Bộ luật Hình sự 2015 và các văn bản hướng dẫn thi hành.\";s:13:\"wpseo_focuskw\";s:34:\"pháp luật hình sự Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:37;a:4:{s:10:\"wpseo_desc\";s:202:\"Tìm hiểu quy định pháp luật Việt Nam về hôn nhân và gia đình: điều kiện kết hôn, ly hôn, quyền và nghĩa vụ của vợ chồng theo Luật Hôn nhân và Gia đình 2014.\";s:13:\"wpseo_focuskw\";s:34:\"pháp luật hôn nhân Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"79\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:39;a:4:{s:10:\"wpseo_desc\";s:224:\"Tìm hiểu pháp luật lao động Việt Nam: quyền và nghĩa vụ của người lao động, hợp đồng lao động, tiền lương, thời giờ làm việc và các quy định theo Bộ luật Lao động 2019.\";s:13:\"wpseo_focuskw\";s:35:\"pháp luật lao động Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:327;a:5:{s:11:\"wpseo_title\";s:14:\"%%term_title%%\";s:10:\"wpseo_desc\";s:233:\"Tìm hiểu hệ thống pháp luật thuế Việt Nam: các loại thuế, nghĩa vụ nộp thuế, chính sách miễn giảm và quy trình kê khai theo quy định của Luật Quản lý thuế và các văn bản liên quan.\";s:13:\"wpseo_focuskw\";s:30:\"pháp luật thuế Việt Nam\";s:13:\"wpseo_linkdex\";s:2:\"76\";s:19:\"wpseo_content_score\";s:2:\"60\";}i:48;a:2:{s:13:\"wpseo_linkdex\";s:5:\"-2803\";s:19:\"wpseo_content_score\";s:1:\"0\";}}}', 'yes'),
